from flask import Flask, request, render_template
import pickle

# Create flask app
flask_app = Flask(__name__)

# Importing our model
model = pickle.load(open("model.pkl", "rb"))
tfidf_vectorizer = pickle.load(open("tfidf_vectorizer.pkl", 'rb'))

stop_fake_news = "data:image/png;base64,"

@flask_app.route("/")
def Home():
    return render_template("index.html", prediction_text = stop_fake_news)

fake = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-3yL_jr1UBncePJns92iPk2SCuQVpQBawYA&usqp=CAU"
real = "data:image/png;base64,"

@flask_app.route("/predict", methods = ["POST"])
def predict():
    raw_news = request.form['rawtext']
    strip_news = [raw_news.strip()]
    input_art = [i for i in strip_news if i]

    try:
        prediction = model.predict(tfidf_vectorizer.transform(input_art).toarray())
        if prediction == [1]:
            filename = real
        else:
            filename = fake
        return render_template("second_page.html", prediction_text=filename)
    except:
        return render_template("index.html", prediction_text=stop_fake_news)

if __name__ == "__main__":
    flask_app.run(debug=True)